def jStatusOrderNumbersData_deferred(shipping_station,
                                     show_backorder,
                                     order_type,
                                    ):
    """
    Returns order numbers of orders in j status; used for testing.
    """
    d = MOCK_DATA[shipping_station]
    max_length = pickpack_constants.MAX_ORDER_NUMBERS

    if order_type == pickpack_constants.TODAY_SURE:
        service_type = 'today_sure'
    elif order_type == pickpack_constants.SIGNATURE_SERVICE:
        service_type = 'signature_service'
    elif order_type == pickpack_constants.SERVICE_FILE:
        service_type = 'service_files'
    elif order_type == pickpack_constants.NORMAL:
        service_type = 'normal'
    else:
        raise pickpack_errors.ApplicationError("Unknown order type" % order_type)

    if show_backorder:
        builder = shopfloor_monitor_result_builders.BackorderOrderNumberBuilder()
        (builder.backorder_ready_to_print,
         builder.backorder_pick_slip_printed,
         builder.backorder_packed,
        ) = parseStatuses(d[service_type]['backorder'], max_length)
    else:
        if order_type == pickpack_constants.SERVICE_FILE:
            builder = shopfloor_monitor_result_builders.OrdinaryOrderNumberBuilderServiceFile()
        else:
            builder = shopfloor_monitor_result_builders.OrdinaryOrderNumberBuilder()
            (builder.can_ship_tomorrow_ready_to_print,
             builder.can_ship_tomorrow_pick_slip_printed,
             builder.can_ship_tomorrow_packed,
            ) = parseStatuses(d[service_type]['can_ship_tomorrow'], max_length)
        (builder.should_ship_today_ready_to_print,
         builder.should_ship_today_pick_slip_printed,
         builder.should_ship_today_packed,
        ) = parseStatuses(d[service_type]['should_ship_today'], max_length)

    if (order_type == pickpack_constants.SERVICE_FILE
        and shipping_station == pickpack_constants.BOTH_SCALE
       ):
        builder.last_print_formatted_order_number = pickpack_constants.NO_DATA_FOUND
        builder.entry_date_of_last_printed_order = pickpack_constants.NO_DATA_FOUND
        builder.entry_time_of_last_printed_order = pickpack_constants.NO_DATA_FOUND
        builder.last_print_date = pickpack_constants.NO_DATA_FOUND
        builder.last_print_time = pickpack_constants.NO_DATA_FOUND
    else:
        builder.last_print_formatted_order_number = 'AA001/00'
        builder.entry_date_of_last_printed_order = '12/11/2014'
        builder.entry_time_of_last_printed_order = '8:10 AM'
        builder.last_print_date = '12/11/2014'
        builder.last_print_time = '8:49 AM'

    return pickpack_data_mock.makeMeADeferred(builder.render())

def parseStatuses(dict_of_orders, max_length):
    """
    Splits a dictionary of order numbers up in to the three statuses: ready to
    print, pick slip printed, and packed. If show until packing bench, returns
    None for the packed status.
    """
    ready_to_print = pickpack_common.truncateList(dict_of_orders['ready_to_print'], max_length)
    pick_slip_printed = pickpack_common.truncateList(dict_of_orders['pick_slip_printed'], max_length)
    packed = pickpack_common.truncateList(dict_of_orders['packed'], max_length)

    return (ready_to_print,
            pick_slip_printed,
            packed,
           )

