# Utility functions

def sum_dict(dict1, dict2):
    """
    Recurse down through two congruent nested dictionaries. Concatenate the
    lists that form the leaf nodes. Return a congruent dictionary where the leaf
    nodes are formed by concatenating the lists of the leaf nodes of dict1 and
    dict2.
    """
    result = {}
    for dict1_key, dict1_value in dict1.items():
        if isinstance(dict1_value, dict):
            if not isinstance(dict2[dict1_key], dict):
                raise pickpack_errors.ApplicationError("Mismatch")
            result[dict1_key] = sum_dict(dict1_value, dict2[dict1_key])
        elif isinstance(dict1_value, list):
            if not isinstance(dict2[dict1_key], list):
                raise pickpack_errors.ApplicationError("Mismatch")
            result[dict1_key] = list(dict1_value)
            result[dict1_key].extend(dict2[dict1_key])
        else:
            raise pickpack_errors.ApplicationError("Unknown type %s" % type(dict1_value))
    return result

def count_items(dictionary):
    """
    Recurse down through a nested dictionary. Count the number of items in the
    lists that form the leaf nodes. Return a congruent dictionary where the leaf
    nodes are the lenghts of the lists that are the leaf nodes of the original
    dictionary.
    """
    result = {}
    for key, value in dictionary.items():
        if isinstance(value, dict):
            result[key] = count_items(value)
        elif isinstance(value, list):
            result[key] = len(value)
        else:
            raise pickpack_errors.ApplicationError("Unknown type %s" % type(value))
    return result



# Accumulate all the orders from the other scales to build the All Scales data.
MOCK_DATA[pickpack_constants.ALL_SCALES] = (
    functools.reduce(sum_dict, [MOCK_DATA[pickpack_constants.CONSUMABLES_SCALE],          # pylint: disable=W0141
                                MOCK_DATA[pickpack_constants.SERVICE_PARTS_SCALE],
                                MOCK_DATA[pickpack_constants.BOTH_SCALE],
                               ]
                    )
)

# Count the number of orders in each category.
MOCK_DATA_COUNTS = count_items(MOCK_DATA)
